namespace FFmpegArgs.Filters.Generated
{
    /// <summary>
    /// ... aevalsrc          |-&gt;A       Generate an audio signal generated by an expression.
    /// </summary>
    public class AevalsrcFilterGen : SourceToAudioFilter
    {
        internal AevalsrcFilterGen(IAudioFilterGraph input) : base("aevalsrc", input)
        {
            AddMapOut();
        }

        /// <summary>
        ///  set the &#39;|&#39;-separated list of channels expressions
        /// </summary>
        public AevalsrcFilterGen exprs(ExpressionValue exprs) => this.SetOption("exprs", (string)exprs);
        /// <summary>
        ///  set the number of samples per requested frame (from 0 to INT_MAX) (default 1024)
        /// </summary>
        public AevalsrcFilterGen nb_samples(int nb_samples) => this.SetOptionRange("nb_samples", nb_samples, 0, INT_MAX);
        /// <summary>
        ///  set the sample rate (default &quot;44100&quot;)
        /// </summary>
        public AevalsrcFilterGen sample_rate(String sample_rate) => this.SetOption("sample_rate", sample_rate.ToString());
        /// <summary>
        ///  set audio duration (default -0.000001)
        /// </summary>
        public AevalsrcFilterGen duration(TimeSpan duration) => this.SetOptionRange("duration", duration, TimeSpan.Zero, TimeSpan.MaxValue);
        /// <summary>
        ///  set channel layout
        /// </summary>
        public AevalsrcFilterGen channel_layout(String channel_layout) => this.SetOption("channel_layout", channel_layout.ToString());
    }

    public static partial class FilterGeneratedExtensions
    {
        /// <summary>
        /// Generate an audio signal generated by an expression.
        /// </summary>
        public static AevalsrcFilterGen AevalsrcFilterGen(this IAudioFilterGraph input) => new AevalsrcFilterGen(input);
    }
}